let b=new Date((new Date).getFullYear()+1,0,1),f=document.getElementById("days"),g=document.getElementById("hours"),h=document.getElementById("minutes"),i=document.getElementById("seconds");function updateClock(e){let t=Math.floor(e/86400);e-=86400*t;let n=Math.floor(e/3600)%24;e-=3600*n;let a=Math.floor(e/60)%60;e-=60*a;let o=Math.floor(e%60);f.innerHTML=padNumber(t),g.innerHTML=padNumber(n),h.innerHTML=padNumber(a),i.innerHTML=padNumber(o)}function padNumber(e){return e<10?"0"+e:e}setInterval(function(){var e=new Date;updateClock((b-e)/1e3)},1e3);